import React, { useEffect, useState } from "react";
import { useQuery } from "react-query";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import TablePagination from "@mui/material/TablePagination";
import SearchBar from "material-ui-search-bar";
import CircularProgress from "@mui/material/CircularProgress";

interface Row {
  name: string;
  _id: string;
  trips: number;
  airline: {
    name: string;
    established: number;
  }[];
}

interface ResponseData {
  data: Row[];
  totalPassengers: number;
}

export function BasicTable() {
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(5);
  const [searchQuery, setSearchQuery] = useState<string>("");
  const [sortColumn, setSortColumn] = useState<string>("");
  // const [sortDirection, setSortDirection] = useState<string>("asc");

  const fetchRows = async (
    page: number,
    rowsPerPage: number,
    // sortColumn: string,
    // sortDirection: string,
    searchQuery: string
  ): Promise<ResponseData> => {
    const url = `https://api.instantwebtools.net/v1/passenger?page=${page}&size=${rowsPerPage}&name=${encodeURIComponent(
      searchQuery
    )}`;
    const response = await fetch(url);
    const data: ResponseData = await response.json();
    return data;
  };

  const {
    data: responseData,
    isLoading,
    isError,
    error,
    isFetching,
  } = useQuery<ResponseData, Error>(
    ["rows", page, rowsPerPage, searchQuery],
    () => fetchRows(page, rowsPerPage, searchQuery),
    {
      keepPreviousData: true,
      retry: 0,
    }
  );

  useEffect(() => {
    setSearchQuery("");
    setPage(0);
  }, [rowsPerPage]);

  const handleChangePage = (event: unknown, newPage: number) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const handleSearch = (searchedVal: string) => {
    setSearchQuery(searchedVal);
  };

  const handleSort = (column: string) => {
    if (sortColumn === column) {
      setSortDirection((prevDirection) =>
        prevDirection === "asc" ? "desc" : "asc"
      );
    } else {
      setSortColumn(column);
      setSortDirection("asc");
    }
  };

  const displayedRows = responseData?.data || [];
  const totalRows = responseData?.totalPassengers || 0;
  const emptyRows = rowsPerPage - displayedRows.length;

  return (
    <Paper>
      <SearchBar
        value={searchQuery}
        onChange={handleSearch}
        onRequestSearch={() => setSearchQuery(searchQuery)}
      />
      {isFetching ? (
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            height: "100vh",
          }}
        >
          <CircularProgress disableShrink />
        </div>
      ) : null}
      {isLoading ? (
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            height: "100%",
          }}
        >
          <CircularProgress disableShrink />
        </div>
      ) : isError ? (
        <div>
          Error:{" "}
          {error instanceof Error ? error.message : "Something went wrong"}
        </div>
      ) : (
        <div style={{ maxHeight: "calc(100vh - 220px)", overflow: "auto" }}>
          <Table sx={{ minWidth: 650 }} aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell>Name</TableCell>
                <TableCell align="right" onClick={() => handleSort("trips")}>
                  Trips
                </TableCell>
                <TableCell
                  align="right"
                  onClick={() => handleSort("airline.name")}
                >
                  Airline Name
                </TableCell>
                <TableCell
                  align="right"
                  onClick={() => handleSort("airline.established")}
                >
                  Established
                </TableCell>
              </TableRow>
            </TableHead>

            <TableBody>
              {displayedRows.map((row: Row) => (
                <TableRow
                  key={row._id}
                  sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
                >
                  <TableCell component="th" scope="row">
                    {row.name}
                  </TableCell>
                  <TableCell align="right">{row.trips}</TableCell>
                  <TableCell align="right">{row.airline[0].name}</TableCell>
                  <TableCell align="right">
                    {row.airline[0].established}
                  </TableCell>
                </TableRow>
              ))}
              {emptyRows > 0 && (
                <TableRow style={{ height: 53 * emptyRows }}>
                  <TableCell colSpan={4} />
                </TableRow>
              )}
            </TableBody>
          </Table>
        </div>
      )}
      <TablePagination
        rowsPerPageOptions={[5, 10, 25]}
        component="div"
        count={totalRows}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
      />
    </Paper>
  );
}
