import React, { useEffect, useState, useCallback } from "react";
import { useQuery } from "react-query";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import TablePagination from "@mui/material/TablePagination";
import SearchBar from "material-ui-search-bar";

interface Row {
  name: string;
  _id: string;
  trips: number;
  airline: {
    name: string;
    established: number;
  }[];
}

export function BasicTable() {
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(5);
  const [searchQuery, setSearchQuery] = useState("");
  const [sortColumn, setSortColumn] = useState<string>("");
  const [sortDirection, setSortDirection] = useState<"asc" | "desc">("asc");

  const fetchRows = useCallback(
    async (page: number) => {
      const sortQuery = sortColumn
        ? `&_sort=${sortColumn}:${sortDirection}`
        : "";
      const response = await fetch(
        `https://api.instantwebtools.net/v1/passenger?page=${page}&size=${rowsPerPage}${sortQuery}`
      );
      const data = await response.json();
      return data.data;
    },
    [rowsPerPage, sortColumn, sortDirection]
  );

  const {
    data: rows,
    isLoading,
    isError,
    error,
    isFetching,
    refetch, // Added refetch function
  } = useQuery<Row[], Error>(["rows", page, sortColumn, sortDirection], () =>
    fetchRows(page)
  );

  useEffect(() => {
    setSearchQuery("");
    setPage(0);
  }, [rowsPerPage]);

  useEffect(() => {
    refetch(); // Trigger new query when sorting parameters change
  }, [refetch, sortColumn, sortDirection]);

  const handleChangePage = (event: unknown, newPage: number) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (
    event: React.ChangeEvent<HTMLInputElement>
  ) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const handleSearch = (searchedVal: string) => {
    setSearchQuery(searchedVal);
  };

  const handleColumnClick = (column: string) => {
    if (column === sortColumn) {
      setSortDirection(sortDirection === "asc" ? "desc" : "asc");
    } else {
      setSortColumn(column);
      setSortDirection("asc");
    }
    setPage(0); // Reset the page to the first page when sorting is changed
  };

  const startIndex = page * rowsPerPage;
  const endIndex = startIndex + rowsPerPage;
  const displayedRows = rows?.slice(startIndex, endIndex) || [];
  const emptyRows = rowsPerPage - (displayedRows?.length || 0);

  return (
    <Paper>
      <SearchBar value={searchQuery} onChange={handleSearch} />
      {isLoading ? (
        <div>Loading...</div>
      ) : isError ? (
        <div>
          Error:{" "}
          {error instanceof Error ? error.message : "Something went wrong"}
        </div>
      ) : (
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell onClick={() => handleColumnClick("name")}>
                Name
              </TableCell>
              <TableCell onClick={() => handleColumnClick("trips")}>
                Trips
              </TableCell>
              <TableCell onClick={() => handleColumnClick("airline.name")}>
                Airline Name
              </TableCell>
              <TableCell
                onClick={() => handleColumnClick("airline.established")}
              >
                Established
              </TableCell>
            </TableRow>
          </TableHead>

          <TableBody>
            {displayedRows.map((row: Row) => (
              <TableRow
                key={row._id}
                sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
              >
                <TableCell component="th" scope="row">
                  {row.name}
                </TableCell>
                <TableCell align="right">{row.trips}</TableCell>
                <TableCell align="right">{row.airline[0].name}</TableCell>
                <TableCell align="right">
                  {row.airline[0].established}
                </TableCell>
              </TableRow>
            ))}
            {emptyRows > 0 && (
              <TableRow style={{ height: 53 * emptyRows }}>
                <TableCell colSpan={4} />
              </TableRow>
            )}
          </TableBody>
        </Table>
      )}
      <TablePagination
        rowsPerPageOptions={[5, 10, 25]}
        component="div"
        count={rows?.length || 0}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
      />
      {isFetching ? <span>Loading...</span> : null}
    </Paper>
  );
}
